jQuery(document).ready(function($){var keywordsIndex=undefined;$(document).on('keyup change keydown','.section-businessNameGenerator__input input',function(){showHideInputTip()});$('.section-businessNameGenerator__form').submit(function(e){e.preventDefault();var $input=$('.section-businessNameGenerator__input input');$('.section-businessNameGenerator__results-name').text(getName($input.val()));$('.section-businessNameGenerator__form').fadeOut(300,function(){$('.section-businessNameGenerator__results').fadeIn(300)})});$('.section-businessNameGenerator__results-getAnotherName').click(function(){var $input=$('.section-businessNameGenerator__input input');$('.section-businessNameGenerator__results-name').text(getName($input.val()))});$('.section-businessNameGenerator__results-restart').click(function(){$('.section-businessNameGenerator__results').fadeOut(300,function(){$('.section-businessNameGenerator__form').fadeIn(300,function(){setTimeout(function(){$('.section-businessNameGenerator__input input').focus()},50)})});$('.section-businessNameGenerator__input input').val('').trigger('change');keywordsIndex=undefined});setTimeout(function(){$('.section-businessNameGenerator__inputTip').removeClass('hide-me')},600);function showHideInputTip(){var $input=$('.section-businessNameGenerator__input input');if(!$input.val()||!$input.val().length){$('.section-businessNameGenerator__inputTip').removeClass('hide-me')}else{$('.section-businessNameGenerator__inputTip').addClass('hide-me')}}
function getName(word){let name=''
const index=keywordsIndex;const keyword=keyWords[index]
if(index===undefined){name=`${word}${getSufix(word)}`}else if('.io'===keyword){name=`${word}${keyword}`}else{name=`${word} ${keyword}`}
if(index===undefined){keywordsIndex=0}else if(index===keyWords.length-1){keywordsIndex=undefined}else{keywordsIndex=index+1}
return name}
const isWordVowell=word=>-1!==['a','e','i','o','u'].indexOf(word.slice(-1).toLowerCase())
const getSufix=word=>(isWordVowell(word)?'ly':'ify')
const keyWords=['Ninja','Buzz','Gorilla','Vapor','Hype','Monkey','Dude','Labz','.io','Planet','Party','Globe','Team','Trade','Group','Cash','Interactive','Records','Gen','Leads','Visions','Centers','Talent','Intel','Crowd','Partners','Innovation','Digi']})