jQuery(document).ready(function($){initCardsMobileSlider('.section-cards__list',899,'.section-cards');function initCardsMobileSlider(sliderClassName,windowWidthPoint,sliderSection){if(!$(sliderClassName).length)return;$(sliderClassName).each(function(){let slider=$(this);$(window).on('resize scroll load',function(){sliderInitialization()});function sliderInitialization(){if($(slider).parents(sliderSection).data('style')=='flat')return;if($(window).width()>windowWidthPoint&&$(slider).hasClass("slick-initialized")){$(slider).slick("unslick")}
if($(window).width()<=windowWidthPoint){if(!$(slider).hasClass("slick-initialized")){$(slider).slick({infinite:!1,slidesToShow:1,slidesToScroll:1,dots:!1,arrows:!1,variableWidth:!0,autoplay:!1,})}}}})}})