jQuery(document).ready(function($){initFaqList();function initFaqList(){let $list=$('.section-faqs__list');let $items=$list.find('.section-faqs__list-item');$items.each(function(){let $itemToggler=$(this).find('.section-faqs__list-itemHeader');$itemToggler.on('click',function(){let $currentItem=$(this).parent();let $prevItem=$list.find('.opened');if(!$currentItem.hasClass('opened')){$prevItem.removeClass('opened').promise().done(function(){$prevItem.find('.section-faqs__list-itemContent').slideUp(250)});$currentItem.addClass('opened').promise().done(function(){$currentItem.find('.section-faqs__list-itemContent').slideDown(250)})}else{$currentItem.removeClass('opened').promise().done(function(){$currentItem.find('.section-faqs__list-itemContent').slideUp(250)})}})})}})