jQuery(document).ready(function($){initMobileNavAccordion();function initMobileNavAccordion(){$('.section-footer__title').on('click',function(e){e.preventDefault();let $currentNav=$(this).parents('.section-footer__nav');let $currentList=$(this).siblings('.section-footer__list');let $currentListUl=$currentList.find('ul');$currentNav.toggleClass('list-opened');if($currentNav.hasClass('list-opened')){let ulHeight=$currentListUl.outerHeight();$currentList.css({'max-height':ulHeight+'px'})}else{$currentList.css({'max-height':'0px'})}});$(window).on('resize',function(){let ww=$(window).width();if(ww<=599){$('.section-footer__nav.list-opened').each(function(){let $currentList=$(this).find('.section-footer__list');let $currentListUl=$currentList.find('ul');let ulHeight=$currentListUl.outerHeight();$currentList.css({'max-height':ulHeight+'px'})})}else{$('.section-footer__nav').each(function(){$(this).find('.section-footer__list').removeAttr('style')})}})}})