jQuery(document).ready(function($){initAdjustContentHeight();function initAdjustContentHeight(){$section=$('.section-imagesAndTextsGrid');if(!$section.length)return;$(window).on('resize load',function(){adjustHeights()})
function adjustHeights(){$section.each(function(){let $currentSection=$(this);let $content=$currentSection.find('.section-imagesAndTextsGrid__listItem-content');let maxContentHeight=0;if($(window).width()<900||$currentSection.data('columns')=='1'||!$currentSection.data('columns')){$content.removeAttr('style');return!1}
$content.each(function(){let childrensHeight=0;$(this).children().each(function(){childrensHeight=childrensHeight+$(this).outerHeight()});if(childrensHeight>maxContentHeight){maxContentHeight=childrensHeight}});$content.css({'max-height':maxContentHeight+'px'})})}}})