jQuery(document).ready(function($){$(window).on('resize load',function(){initTableRowsHeight();initMobileSlider('.section-pricingPlans__inner',599)});initPriceToggler();function initTableRowsHeight(){if(!$('.section-pricingPlans').length)return;let $detailColCells=$('.details-col').find('.section-pricingPlans__cell');$detailColCells.each(function(){let cellIndex=$(this).index()+1;let cellHeight=$(this).outerHeight()
if($(window).width()>=600){$('.plan-col .section-pricingPlans__cell:nth-child('+cellIndex+'):not(.cta-cell):not(.plan-cell)').css({'height':cellHeight})}else{$('.plan-col .section-pricingPlans__cell:nth-child('+cellIndex+'):not(.cta-cell):not(.plan-cell)').attr('style','')}})}
function initMobileSlider(sliderClassName,windowWidthPoint){if(!$(sliderClassName).length)return;if($(window).width()>windowWidthPoint&&$(sliderClassName).hasClass("slick-initialized")){$(sliderClassName).slick("unslick");$('.plan-col[data-slide-order="1"]').appendTo(sliderClassName)}
if($(window).width()<=windowWidthPoint){if(!$(sliderClassName).hasClass("slick-initialized")){$('.plan-col[data-slide-order="2"]').appendTo(sliderClassName);$(sliderClassName).slick({infinite:!1,slidesToShow:1,slidesToScroll:1,dots:!1,arrows:!1,autoplay:!1,slide:'.plan-col',variableWidth:!0,})}}}
function initPriceToggler(){if(!$('.section-pricingPlans').length)return;$('.section-pricingPlans').each(function(){let currentPlansTable=this;$(currentPlansTable).find('.plan-col').each(function(){let currentPlan=$(this);let activePeriodType=$(currentPlan).find('.section-pricingPlans__cta-periodToggle').attr('data-active-period-type');setActivePeriod(activePeriodType,currentPlan);$(currentPlan).find('.section-pricingPlans__cta-periodToggle').on('click',function(){let activePeriodType;if($(this).attr('data-active-period-type')=='1'){$(this).attr('data-active-period-type',2);activePeriodType=2}else if($(this).attr('data-active-period-type')=='2'){$(this).attr('data-active-period-type',1);activePeriodType=1}
setActivePeriod(activePeriodType,currentPlan)})});function setActivePeriod(periodType,currentPlan){let $activePeriod=$(currentPlan).find('.section-pricingPlans__cta-periodName[data-period-type="'+periodType+'"]');let activePeriodPrice=$activePeriod.data('period-price');let activePeriodPostfix=$activePeriod.data('period-postfix');let $activePeriodSave=$(currentPlan).find('.section-pricingPlans__save[data-save-period="'+periodType+'"]');$('.section-pricingPlans__save').removeClass('active-save');$('.active-period').removeClass('active-period');$activePeriodSave.addClass('active-save');$activePeriod.addClass('active-period');$(currentPlan).find('.section-pricingPlans__cta-priceNumber').text(activePeriodPrice);$(currentPlan).find('.section-pricingPlans__cta-pricePostfix').text(activePeriodPostfix)}})}})