jQuery(document).ready(function($){initBlogNavScroll();initShareButtons('data-share');initBlogCategoriesAjax();function initBlogNavScroll(){if(!$('.blog-nav').length)return;let $blogNav=$('.blog-nav');let $header=$('.section-header');$header.attr('data-header-highlight','none');$(window).on('scroll load',function(){let headerHeight=$header.outerHeight();let navOffsetTop=$blogNav[0].getBoundingClientRect().top
if(navOffsetTop<=headerHeight){$blogNav.addClass('nav-sticked')}else{$blogNav.removeClass('nav-sticked')}})}
function initShareButtons(shareAttrName='data-share'){let shareButtons=$('['+shareAttrName+']');if(!$(shareButtons).length)return;$(shareButtons).each(function(){let button=$(this);let socialName=$(button).attr(shareAttrName);let socialTitle=$(button).attr(shareAttrName+'-title');let url=getShareUrl(socialName,socialTitle);$(button).attr('href',url)});initSocialSharePopup(shareButtons)
function getShareUrl(shareName,shareTitle=null){const page={url:window.location.href,title:shareTitle||document.title,}
const shareList={facebook:{url:`https://www.facebook.com/sharer/sharer.php?u=${page.url}&t=${page.title}`,},twitter:{url:`https://twitter.com/share?url=${page.url}&text=${page.title}`,},linkedin:{url:`https://www.linkedin.com/shareArticle?mini=true&url=${page.url}&t=${page.title}`,},email:{url:`mailto:?subject=${page.title}&amp;body=${page.url}`,},whatsapp:{url:`whatsapp://send?text=${page.title} ${page.url}`,}}
return shareList[shareName].url}
function initSocialSharePopup(shareButtonsSelector){$(shareButtonsSelector).click(function(e){e.preventDefault();let href=$(this).attr('href');let title=$(this).attr(shareAttrName+'-title');PopupCenter(href,title,500,300)});function PopupCenter(url,title,w,h){let dualScreenLeft=window.screenLeft!=undefined?window.screenLeft:screen.left;let dualScreenTop=window.screenTop!=undefined?window.screenTop:screen.top;let width=window.innerWidth?window.innerWidth:document.documentElement.clientWidth?document.documentElement.clientWidth:screen.width;let height=window.innerHeight?window.innerHeight:document.documentElement.clientHeight?document.documentElement.clientHeight:screen.height;let left=((width/2)-(w/2))+dualScreenLeft;let top=((height/2)-(h/2))+dualScreenTop;let newWindow=window.open(url,title,'scrollbars=yes, width='+w+', height='+h+', top='+top+', left='+left);if(window.focus){newWindow.focus()}}}}
function initBlogCategoriesAjax(){$(document).on('click','.blog-nav__listItem a',function(e){e.preventDefault();$('html,body').animate({scrollTop:$(".blog-articles-ajax").offset().top-($('.section-header').height()+$('.blog-nav').height())},1000);$('.blog-nav__listItem').removeClass('nav-active');$(this).parent().addClass('nav-active');loadBlogAjax($(this).attr('href'))});$(document).on('click','.blog-articles-ajax .nav-links a',function(e){e.preventDefault();$('html,body').animate({scrollTop:$(".blog-articles-ajax").offset().top-($('.section-header').height()+$('.blog-nav').height())},1000);loadBlogAjax($(this).attr('href'))});function loadBlogAjax(urlToLoad){$('.blog-articles-ajax').addClass('is-loading');$('.blog-articles-ajax .blog-ajax').addClass('to-delete');$('.blog-articles-ajax .blog-ajax-wrapper').load(urlToLoad+" .blog-ajax",function(){$('.blog-articles-ajax .blog-ajax.to-delete').remove();$('.blog-articles-ajax').removeClass('is-loading')})}}})