jQuery(document).ready(function($){initFaqList();initFaqFilters();function initFaqList(){let $list=$('.faqs-posts__list');let $items=$list.find('.faqs-posts__list-item');$items.each(function(){let $itemToggler=$(this).find('.faqs-posts__list-itemHeader');$itemToggler.on('click',function(){let $currentItem=$(this).parent();let $prevItem=$list.find('.opened');if(!$currentItem.hasClass('opened')){$prevItem.removeClass('opened').promise().done(function(){$prevItem.find('.faqs-posts__list-itemContent').slideUp(250)});$currentItem.addClass('opened').promise().done(function(){$currentItem.find('.faqs-posts__list-itemContent').slideDown(250)})}else{$currentItem.removeClass('opened').promise().done(function(){$currentItem.find('.faqs-posts__list-itemContent').slideUp(250)})}})})}
function initFaqFilters(){let $container=$('.faqs-posts__list');let mixObject={animation:{duration:250,effects:'fade translateZ(-100px)'}}
if(getUrlParam('category')){mixObject.load={filter:'.'+getUrlParam('category')}}
let mixer=mixitup($container,mixObject)}
function getUrlParam(name,url){if(!url)url=window.location.href;name=name.replace(/[\[\]]/g,"\\$&");var regex=new RegExp("[?&]"+name+"(=([^&#]*)|&|#|$)"),results=regex.exec(url);if(!results)return null;if(!results[2])return'';return decodeURIComponent(results[2].replace(/\+/g," "))}})